                    function showPinPopup() {
                        $.afui.popup({
                            title: "Masukkan PIN BebasBayar Anda",
                            message: "Pin: <input type='password' maxlength='6' class='af-ui-forms' id='ispin'>",
                            cancelText: "Tutup",
                            cancelCallback: function () {
                                window.location.href = "https://m.bebasbayar.com:443/";
                            },
                            doneText: "Verifikasi",
                            doneCallback: function () {
                                cekPin($('#ispin').val());
                                return false;
                            },
                            cancelOnly: false
                        });
                    }
                    function cekPin(userpin) {
                        if (userpin.length < 6) {
                            console.log("PIN tidak valid. Isikan minimal 6 digit");
                            return false;
                        }
                        $.ajax("submit/verify_pin", {
                            type: 'POST',
                            dataType: 'json',
                            timeout: 60000,
                            data: {'pin': userpin}
                        }).done(function (response) {
                            console.log(response.error);
                            if (!response.error) {
                                growl(response.result, 'info');
                            } else {
                                halt('Informasi', response.result, tutup);
                            }
//                            return false;
                        });
//                        return false;
                    }
                    function tutup() {
                        showPinPopup();
                    }
    </script>
